<?phpnamespace Nemundo\Content\App\Stream\Action;use Nemundo\Content\Action\AbstractContentAction;use Nemundo\Content\App\Stream\Data\Stream\Stream;use Nemundo\Content\Type\AbstractContentItem;class StreamContentAction extends AbstractContentAction{    protected function loadAction()    {        $this->actionLabel='Stream';        $this->actionLabel='Send to Stream';        $this->actionId='b0f47f44-c80b-46e1-b01b-3a636ada8edd';    }    public function onAction(AbstractContentItem $item)    {        $data=new Stream();        $data->contentId= $item->getContentId();        $data->hasMessage=false;        $data->active=true;        $data->save();    }}