<?phpnamespace Nemundo\Content\App\Stream\Page;use Nemundo\Admin\Com\Table\AdminTable;use Nemundo\Admin\Com\Table\Row\AdminTableRow;use Nemundo\Com\Template\AbstractTemplateDocument;use Nemundo\Content\App\Stream\Collection\StreamContentTypeCollection;use Nemundo\Content\App\Stream\Com\Form\StreamContentTypeForm;use Nemundo\Content\App\Stream\Data\StreamContentType\StreamContentTypeReader;use Nemundo\Content\App\Stream\Template\StreamAdminTemplate;use Nemundo\Content\Com\Admin\ContentTypeCollectionAdmin;class ConfigPage extends StreamAdminTemplate{    public function getContent()    {        /*$admin = new ContentTypeCollectionAdmin($this);        $admin->contentTypeCollection = new StreamContentTypeCollection();*/        $form=new StreamContentTypeForm($this);        $table=new AdminTable($this);        $reader = new StreamContentTypeReader();        $reader->model->loadContentType();        foreach ($reader->getData() as $contentTypeRow) {            $row=new AdminTableRow($table);            $row->addText($contentTypeRow->contentType->contentType);        }        return parent::getContent();    }}