<?phpnamespace Nemundo\Content\App\Text\Content\Html;use Nemundo\Admin\Com\ListBox\AdminLargeTextBox;use Nemundo\Content\App\Text\Content\LargeText\LargeTextItem;use Nemundo\Content\Form\AbstractContentForm;class HtmlForm extends AbstractContentForm{    /**     * @var AdminLargeTextBox     */    private $html;    public function getContent()    {        $this->html = new AdminLargeTextBox($this);        $this->html->label = 'Html';        return parent::getContent();    }    protected function loadUpdateForm()    {        $largeTextRow = (new LargeTextItem($this->dataId))->getDataRow();        $this->html->value = $largeTextRow->largeText;    }    protected function onSave()    {        $builder = new HtmlBuilder($this->dataId);        $builder->largeText = $this->html->getValue();        $builder->addActionList($this->getActionList());        $builder->buildContent();    }}