<?phpnamespace Nemundo\Content\App\Text\Content\LargeText;use Nemundo\Admin\Com\ListBox\AdminLargeTextBox;use Nemundo\Content\Form\AbstractContentForm;use Nemundo\Package\Bootstrap\FormElement\BootstrapLargeTextBox;class LargeTextForm extends AbstractContentForm  // AbstractLargeTextContentForm{    /**     * @var AdminLargeTextBox     */    private $largeText;    public function getContent()    {        $this->largeText = new AdminLargeTextBox($this);        $this->largeText->label = 'Text';        return parent::getContent(); // TODO: Change the autogenerated stub    }    protected function loadUpdateForm()    {        $largeTextRow = (new LargeTextItem($this->dataId))->getDataRow();        $this->largeText->value = $largeTextRow->largeText;    }    protected function onSave()    {        $builder = new LargeTextBuilder($this->dataId);        $builder->largeText = $this->largeText->getValue();        $builder->addActionList($this->getActionList());        $builder->buildContent();        //$this->runAction((new LargeTextItem($builder->getDataId())));    }    /*protected function onSubmit()    {        $this->contentType->largeText = $this->largeText->getValue();        $this->contentType->saveType();    }*/}