<?phpnamespace Nemundo\Content\App\Text\Content\LargeText;use Nemundo\Admin\Com\ListBox\AdminLargeTextBox;use Nemundo\Content\App\Text\Content\LargeText\Builder\LargeTextBuilder;use Nemundo\Content\Form\AbstractContentForm;class LargeTextForm extends AbstractContentForm{    /**     * @var AdminLargeTextBox     */    private $largeText;    public function getContent()    {        $this->largeText = new AdminLargeTextBox($this);        $this->largeText->label = 'Text';        return parent::getContent();    }    protected function loadUpdateForm()    {        $largeTextRow = (new LargeTextItem($this->dataId))->getDataRow();        $this->largeText->value = $largeTextRow->largeText;    }    protected function onSave()    {        $builder = new LargeTextBuilder($this->dataId);        $builder->largeText = $this->largeText->getValue();        $builder->addActionList($this->getActionList());        $builder->buildContent();    }}