<?phpnamespace Nemundo\Content\App\Text\Content\RichText;use Nemundo\Admin\Com\ListBox\AdminHtmlEditor;use Nemundo\Content\Form\AbstractContentForm;use Nemundo\Package\Bootstrap\FormElement\BootstrapLargeTextBox;class RichTextForm extends AbstractContentForm{    /**     * @var BootstrapLargeTextBox     */    private $text;    public function getContent()    {        $this->text = new AdminHtmlEditor($this);        $this->text->label = 'Text';        return parent::getContent();    }    protected function loadUpdateForm()    {        $largeTextRow = (new RichTextItem($this->dataId))->getDataRow();        $this->text->value = $largeTextRow->largeText;    }    protected function onSave()    {        $builder = new RichTextBuilder($this->dataId);        $builder->largeText = $this->text->getValue();        $builder->addActionList($this->getActionList());        $builder->buildContent();    }}