<?phpnamespace Nemundo\Content\App\Text\Content\RichText;use Nemundo\Content\View\AbstractContentView;use Nemundo\Html\Block\ContentDiv;class RichTextView extends AbstractContentView{    /**     * @var RichTextType     */    public $contentType;    protected function loadView()    {        $this->viewName = 'default';        $this->viewId = '201dc107-4e65-4345-92d1-a63ff154dcea';        $this->defaultView = true;    }    public function getContent()    {        $largeTextRow = (new RichTextItem($this->dataId))->getDataRow();        $div = new ContentDiv($this);        $div->content = $largeTextRow->largeText;        return parent::getContent();    }}