<?phpnamespace Nemundo\Content\App\Text\Content\Text;use Nemundo\Admin\Com\ListBox\AdminTextBox;use Nemundo\Content\Form\AbstractContentForm;class TextForm extends AbstractContentForm{    /**     * @var AdminTextBox     */    protected $text;    public function getContent()    {        $this->text = new AdminTextBox($this);        $this->text->label = 'Text';        $this->text->validation = true;        return parent::getContent();    }    protected function loadUpdateForm()    {        $textRow = (new TextItem($this->dataId))->getDataRow();        $this->text->value = $textRow->text;    }    protected function onSave()    {        $builder = new TextBuilder($this->dataId);        $builder->text = $this->text->getValue();        $builder->addActionList($this->getActionList());        $builder->buildContent();    }}