<?phpnamespace Nemundo\Content\App\Text\Content\Text\View;use Nemundo\Content\App\Text\Content\Text\TextItem;use Nemundo\Content\View\AbstractContentView;use Nemundo\Html\Block\ContentDiv;class TextView extends AbstractContentView{    /**     * @var TextType     */    //public $contentType;    protected function loadView()    {        $this->viewName='default';        $this->viewId='f4da9caa-e07c-4974-ac12-1f53c24fe8cb';        $this->defaultView=true;    }    public function getContent()    {        $textRow = (new TextItem($this->dataId))->getDataRow();  // $this->contentType->getDataRow();        $p = new ContentDiv($this);        $p->content = $textRow->text;        return parent::getContent();    }}