<?phpnamespace Nemundo\Content\App\Text\Install;use Nemundo\App\Application\Type\Install\AbstractInstall;use Nemundo\App\WebService\Setup\ServiceRequestSetup;use Nemundo\Content\App\Text\Application\TextApplication;use Nemundo\Content\App\Text\Content\Html\HtmlType;use Nemundo\Content\App\Text\Content\LargeText\LargeTextType;use Nemundo\Content\App\Text\Content\RichText\RichTextType;use Nemundo\Content\App\Text\Content\Text\TextType;use Nemundo\Content\App\Text\Data\TextModelCollection;use Nemundo\Content\App\Text\Service\HtmlPostService;use Nemundo\Content\App\Text\Service\LargeTextService;use Nemundo\Content\App\Text\Service\TextPostService;use Nemundo\Content\Application\ContentApplication;use Nemundo\Content\Setup\ContentTypeSetup;use Nemundo\Model\Setup\ModelCollectionSetup;class TextInstall extends AbstractInstall{    public function install()    {        (new ContentApplication())->installApp();        (new ModelCollectionSetup())            ->addCollection(new TextModelCollection());        (new ContentTypeSetup(new TextApplication()))            ->addContentType(new TextType())            ->addContentType(new LargeTextType())            ->addContentType(new RichTextType())            ->addContentType(new HtmlType());        /*(new ContentTypeSetup(new TextApplication()))            ->addContentTypeCollection(new TextContentTypeCollection());*/        (new ServiceRequestSetup(new TextApplication()))            ->addService(new TextPostService())            ->addService(new LargeTextService())            ->addService(new HtmlPostService());    }}