<?phpnamespace Nemundo\Content\App\Text\Service;use Nemundo\Content\App\Text\Content\Html\HtmlType;use Nemundo\Content\Service\AbstractContentServiceRequest;use Nemundo\Core\Http\Request\HttpRequest;class HtmlPostService extends AbstractContentServiceRequest{    protected function loadService()    {        $this->serviceName = 'html-post';    }    public function onRequest()    {        $id = null;        $request = new HttpRequest('id');        if ($request->hasValue()) {            $id = $request->getValue();        }        $contentType = new HtmlType($id);        $contentType->html = (new HttpRequest('text'))->getValue();        $contentType->saveType();        //$this->saveTree($contentType);        $this->sendContentOkStatus($contentType);    }}