<?phpnamespace Nemundo\Content\App\Timeline\Site;use Nemundo\Admin\Site\AbstractClearIconSite;use Nemundo\Content\Parameter\ContentTypeParameter;use Nemundo\Core\Http\Url\UrlRedirect;use Nemundo\Core\Http\Url\UrlReferer;use Nemundo\Web\Url\UrlParameterBuilder;class ClearSite extends AbstractClearIconSite{    /**     * @var ClearSite     */    public static $site;    protected function loadSite()    {        ClearSite::$site = $this;    }    public function loadContent()    {        $urlBuilder = (new UrlParameterBuilder((new UrlReferer())->getUrl()))->removeParameter(new ContentTypeParameter());        (new UrlRedirect())->redirect($urlBuilder->getUrl());    }}