<?phpnamespace Nemundo\Content\App\Video\Content\YouTube;use Nemundo\Admin\Com\ListBox\AdminLargeTextBox;use Nemundo\Admin\Com\ListBox\AdminNumberBox;use Nemundo\Admin\Com\ListBox\AdminTextBox;use Nemundo\Content\App\Explorer\Content\Container\ContainerItem;use Nemundo\Content\Form\AbstractContentForm;// Fehlermeldung, falls kein v= vorhanden istclass YouTubeForm extends AbstractContentForm{    /**     * @var AdminTextBox     */    private $url;    /**     * @var AdminTextBox     */    private $videoTitle;    /**     * @var AdminLargeTextBox     */    private $description;    /**     * @var AdminNumberBox     */    private $start;    public function getContent()    {        $this->url = new AdminTextBox($this);        $this->url->label = 'YouTube Url';        $this->url->validation = true;        $this->videoTitle = new AdminTextBox($this);        $this->videoTitle->label = 'Title';        $this->description = new AdminLargeTextBox($this);        $this->description->label = 'Description';        $this->start = new AdminNumberBox($this);        $this->start->label = 'Start';        $this->start->value = 0;        return parent::getContent();    }    protected function loadUpdateForm()    {        $this->url->disabled = true;        $this->url->validation = false;        $videoRow = (new YouTubeItem($this->dataId))->getDataRow();        $this->videoTitle->value = $videoRow->title;        $this->description->value = $videoRow->description;        $this->start->value = $videoRow->start;    }    protected function onSave()    {        $builder = new YouTubeBuilder($this->dataId);        $builder->addActionList($this->getActionList());        $builder->fromUrl($this->url->getValue());    }    protected function onUpdate()    {        $builder = new YouTubeBuilder($this->dataId);        $builder->title = $this->videoTitle->getValue();        $builder->description = $this->description->getValue();        $builder->start=$this->start->getValue();        $builder->addActionList($this->getActionList());        $builder->buildContent();    }}