<?phpnamespace Nemundo\Content\App\Video\Content\YouTube;use Nemundo\Content\App\Video\Data\YouTube\YouTubeReader;use Nemundo\Content\App\Video\Data\YouTube\YouTubeRow;use Nemundo\Content\Type\AbstractContentItem;class YouTubeItem extends AbstractContentItem{    public function loadItem()    {        $this->contentType = new YouTubeType();    }    protected function onDataRow()    {        $this->dataRow = (new YouTubeReader())->getRowById($this->getDataId());    }    /**     * @return \Nemundo\Model\Row\AbstractModelDataRow|YouTubeRow     */    public function getDataRow()    {        return parent::getDataRow();    }    public function getSubject()    {        return $this->getDataRow()->title;    }    public function getText()    {        return $this->getDataRow()->description;    }    public function getData()    {        $data = [];        $data['title'] = $this->getDataRow()->title;        $data['decription'] = $this->getDataRow()->description;        $data['video_id'] = $this->getDataRow()->youtubeId;        return $data;    }}