<?phpnamespace Nemundo\Content\App\Webcam\Content\Webcam;use Nemundo\Content\View\AbstractContentView;use Nemundo\Html\Formatting\Small;use Nemundo\Package\Bootstrap\Image\BootstrapResponsiveImage;use Nemundo\Package\Bootstrap\Layout\Grid\BootstrapRow;class WebcamContentView extends AbstractContentView{    /**     * @var WebcamContentType     */    public $contentType;    protected function loadView()    {        // TODO: Implement loadView() method.        $this->viewName='Default';        $this->viewId='fedd070a-d090-4bb8-967c-898576fa4a60';        $this->defaultView=true;    }    public function getContent()    {        $webcamRow = $this->contentType->getDataRow();        $row = new BootstrapRow($this);        $img = new BootstrapResponsiveImage($row);        $img->src = $webcamRow->imageUrl;        if ($webcamRow->sourceUrl !== '') {            $row = new BootstrapRow($this);            $small = new Small($row);            $small->content = 'Quelle: ' . $webcamRow->sourceUrl;        }        return parent::getContent();    }}