<?phpnamespace Nemundo\Content\App\Calendar\Site;use Nemundo\Content\App\Calendar\Page\CalendarNewPage;use Nemundo\Web\Site\AbstractSite;class CalendarNewSite extends AbstractSite{    /**     * @var CalendarNewSite     */    public static $site;    protected function loadSite()    {        $this->title = 'New';        $this->url = 'new';        CalendarNewSite::$site = $this;    }    public function loadContent()    {        (new CalendarNewPage())->render();    }}